mapper.update:

	update tb_voucher_manager
	set use_yn=0
	where no = #{no };


	update tb_voucher_item
	set use_yn=0
	where voucher_no = #{no };


	update tb_voucher_system_mapping
	set use_yn=0
	where voucher_no = #{no };

mapper.select:

	select insert_userid, insert_day, insert_team
	  from tb_voucher_manager
	 where no = #{no };

mapper.update:

	update tb_voucher_manager
	   set use_yn = 0
	 where no = #{no };


	update tb_voucher_item
	   set use_yn = 0
	 where voucher_no = #{no };


	update tb_voucher_system_mapping
	   set use_yn = 0
	 where voucher_no = #{no };
