select.if:
			
				select ((select count(*) as count
						   from tb_customer
		   				  where name!='여비'
				

						    
					
						and delete_flag= #{deleteFlag }
					
				
				 		    
					
						and insert_day >= #{datepicker1 }
					
				
						    
					
						and insert_day <= #{datepicker2 }
					
				
				
					
						and no like '%${no }%'
						   
				
						  
			
						   ) +
					    (select count(*) as count
				    	   from tb_purchasecustomer
				

						    
					
						where delete_flag= #{deleteFlag }
					
				
				 		    
					
						and insert_day >= #{datepicker1 }
					
				
						    
					
						and insert_day <= #{datepicker2 }
					
				
				
					
						and no like '%${no }%'
						   
				
			
			
				    	   ) +
					    (select count(*) as count
				    	   from tb_salescustomer
				

						    
					
						where delete_flag= #{deleteFlag }
					
				
				 		    
					
						and insert_day >= #{datepicker1 }
					
				
						    
					
						and insert_day <= #{datepicker2 }
					
				
				
					
						and no like '%${no }%'
						   
				
			
			
				    	   
				    	   )) as count
			 	  from dual


	select count(*) as count
	  from tb_customer


			    
		
			where delete_flag= #{deleteFlag }
		
	
			    
		
			and insert_day >= #{datepicker1 }
		
	
			    
		
			and insert_day <= #{datepicker2 }
		
	
	
		
			and no like '%${no }%'


	select count(*) as count
	  from tb_purchasecustomer


			    
		
			where delete_flag= #{deleteFlag }
		
	
			    
		
			and insert_day >= #{datepicker1 }
		
	
			    
		
			and insert_day <= #{datepicker2 }
		
	
	
		
			and no like '%${no }%'


	select count(*) as count
	  from tb_salescustomer


			    
		
			where delete_flag= #{deleteFlag }
		
	
			    
		
			and insert_day >= #{datepicker1 }
		
	
			    
		
			and insert_day <= #{datepicker2 }
		
	
	
		
			and no like '%${no }%'

select replace(left(no, 1), "A", "자산거래처") as customerDiv, (right(no, 12)) as no, name as name, ceo as ceo, address as address, detail_address as detailAddress, conditions as conditions, item as item, corporation_no as corporationNo, open_date as openDate, jurisdiction_office as jurisdictionOffice, phone as phone, manager_name as managerName, manager_email as managerEmail, deposit_no as depositNo, deposit_host as depositHost, insert_userid as insertUserid, insert_day as insertDay, update_userid as updateUserid, update_day as updateDay from tb_customer where name!='여비'
and delete_flag = #{vo.deleteFlag }
and insert_day >= #{vo.datepicker1 }
and insert_day <= #{vo.datepicker2 }
and no like '%${vo.no }%'
union select replace(left(no, 1), "P", "매입 거래처") as customerDiv, (right(no, 12)) as no, name as name, ceo as ceo, address as address, detail_address as detailAddress, conditions as conditions, item as item, corporation_no as corporationNo, open_date as openDate, jurisdiction_office as jurisdictionOffice, phone as phone, manager_name as managerName, manager_email as managerEmail, deposit_no as depositNo, deposit_host as depositHost, insert_userid as insertUserid, insert_day as insertDay, update_userid as updateUserid, update_day as updateDay from tb_purchasecustomer
where delete_flag = #{vo.deleteFlag }
and insert_day >= #{vo.datepicker1 }
and insert_day <= #{vo.datepicker2 }
and no like '%${vo.no }%'
union select replace(left(no, 1), "S", "매출 거래처") as customerDiv, (right(no, 12)) as no, name as name, ceo as ceo, address as address, detail_address as detailAddress, conditions as conditions, item as item, corporation_no as corporationNo, open_date as openDate, jurisdiction_office as jurisdictionOffice, phone as phone, manager_name as managerName, manager_email as managerEmail, deposit_no as depositNo, deposit_host as depositHost, insert_userid as insertUserid, insert_day as insertDay, update_userid as updateUserid, update_day as updateDay from tb_salescustomer
where delete_flag = #{vo.deleteFlag }
and insert_day >= #{vo.datepicker1 }
and insert_day <= #{vo.datepicker2 }
and no like '%${vo.no }%'
order by corporationNo desc limit #{pagination.pageIndex }, #{pagination.listSize }

