select.if:
			
				select ((select count(*) as count
						   from tb_customer
			  			  where name!='여비'
				

						    
					
						and delete_flag= #{deleteFlag }
					
				
				 		    
					
						and insert_day >= #{datepicker1 }
					
				
						    
					
						and insert_day <= #{datepicker2 }
					
				
				
					
						and no like '%${no }%'
						   
				
						  
			
						   ) +
					    (select count(*) as count
				    	   from tb_purchasecustomer
				

						    
					
						where delete_flag= #{deleteFlag }
					
				
				 		    
					
						and insert_day >= #{datepicker1 }
					
				
						    
					
						and insert_day <= #{datepicker2 }
					
				
				
					
						and no like '%${no }%'
						   
				
			
			
				    	   ) +
					    (select count(*) as count
				    	   from tb_salescustomer
				

						    
					
						where delete_flag= #{deleteFlag }
					
				
				 		    
					
						and insert_day >= #{datepicker1 }
					
				
						    
					
						and insert_day <= #{datepicker2 }
					
				
				
					
						and no like '%${no }%'
						   
				
			
			
				    	   
				    	   )) as count
			 	  from dual


	select count(*) as count
	  from tb_customer
	  where assets_flag != "e"


			    
		
			and delete_flag= #{deleteFlag }
		
	
			    
		
			and insert_day >= #{datepicker1 }
		
	
			    
		
			and insert_day <= #{datepicker2 }
		
	
	
		
			and no like '%${no }%'


	select count(*) as count
	  from tb_customer
	  where assets_flag = "e"
	  and name != '여비'


			    
		
			and delete_flag= #{deleteFlag }
		
	
			    
		
			and insert_day >= #{datepicker1 }
		
	
			    
		
			and insert_day <= #{datepicker2 }
		
	
	
		
			and no like '%${no }%'


	select count(*) as count
	  from tb_purchasecustomer


			    
		
			where delete_flag= #{deleteFlag }
		
	
			    
		
			and insert_day >= #{datepicker1 }
		
	
			    
		
			and insert_day <= #{datepicker2 }
		
	
	
		
			and no like '%${no }%'


	select count(*) as count
	  from tb_salescustomer


			    
		
			where delete_flag= #{deleteFlag }
		
	
			    
		
			and insert_day >= #{datepicker1 }
		
	
			    
		
			and insert_day <= #{datepicker2 }
		
	
	
		
			and no like '%${no }%'

select replace(left(no, 1), "A", "자산거래처") as customerDiv, (CASE CHAR_LENGTH(no) when "07" then no when "11" then concat(substr(substr(no, 2), 1, 3), "-", substr(substr(no,2), 4, 2), "-" , substr(substr(no, 2), 6, 5)) end) as no, name as name, ceo as ceo, address as address, detail_address as detailAddress, conditions as conditions, item as item, corporation_no as corporationNo, open_date as openDate, jurisdiction_office as jurisdictionOffice, (IFNULL(CASE LEFT(phone,2) WHEN "02" THEN CASE CHAR_LENGTH(phone) WHEN "10" THEN concat("02","-",substring(phone,3,4),"-",RIGHT(phone,4)) WHEN "11" THEN concat("02","-",substring(phone,3,3),"-",RIGHT(phone,4)) END ELSE CASE CHAR_LENGTH(phone) WHEN "10" THEN concat(LEFT(phone,3),"-",substring(phone,4,3),"-",RIGHT(phone,4)) WHEN "11" THEN concat(LEFT(phone,3),"-",substring(phone,4,4),"-",RIGHT(phone,4)) WHEN "8" THEN concat(LEFT(phone,4),"-",substring(phone,4,4)) END END,"없음")) as phone, manager_name as managerName, manager_email as managerEmail, deposit_no as depositNo, deposit_host as depositHost, insert_userid as insertUserid, insert_day as insertDay, update_userid as updateUserid, update_day as updateDay from tb_customer where assets_flag != 'e'
and delete_flag = #{vo.deleteFlag }
and insert_day >= #{vo.datepicker1 }
and insert_day <= #{vo.datepicker2 }
and no like '%${vo.no }%'
union select replace(left(no, 1), "P", "매입 거래처") as customerDiv, concat(substr(substr(no, 2), 1, 3), "-", substr(substr(no,2), 4, 2), "-" , substr(substr(no, 2), 6, 5)) as no, name as name, ceo as ceo, address as address, detail_address as detailAddress, conditions as conditions, item as item, corporation_no as corporationNo, open_date as openDate, jurisdiction_office as jurisdictionOffice, (IFNULL(CASE LEFT(phone,2) WHEN "02" THEN CASE CHAR_LENGTH(phone) WHEN "10" THEN concat("02","-",substring(phone,3,4),"-",RIGHT(phone,4)) WHEN "11" THEN concat("02","-",substring(phone,3,3),"-",RIGHT(phone,4)) END ELSE CASE CHAR_LENGTH(phone) WHEN "10" THEN concat(LEFT(phone,3),"-",substring(phone,4,3),"-",RIGHT(phone,4)) WHEN "11" THEN concat(LEFT(phone,3),"-",substring(phone,4,4),"-",RIGHT(phone,4)) WHEN "8" THEN concat(LEFT(phone,4),"-",substring(phone,4,4)) END END,"없음")) as phone, manager_name as managerName, manager_email as managerEmail, deposit_no as depositNo, deposit_host as depositHost, insert_userid as insertUserid, insert_day as insertDay, update_userid as updateUserid, update_day as updateDay from tb_purchasecustomer
where delete_flag = #{vo.deleteFlag }
and insert_day >= #{vo.datepicker1 }
and insert_day <= #{vo.datepicker2 }
and no like '%${vo.no }%'
union select concat('은행') as customerDiv, no as no, name as name, ceo as ceo, address as address, detail_address as detailAddress, conditions as conditions, item as item, corporation_no as corporationNo, open_date as openDate, jurisdiction_office as jurisdictionOffice, (IFNULL(CASE LEFT(phone,2) WHEN "02" THEN CASE CHAR_LENGTH(phone) WHEN "10" THEN concat("02","-",substring(phone,3,4),"-",RIGHT(phone,4)) WHEN "11" THEN concat("02","-",substring(phone,3,3),"-",RIGHT(phone,4)) END ELSE CASE CHAR_LENGTH(phone) WHEN "10" THEN concat(LEFT(phone,3),"-",substring(phone,4,3),"-",RIGHT(phone,4)) WHEN "11" THEN concat(LEFT(phone,3),"-",substring(phone,4,4),"-",RIGHT(phone,4)) WHEN "8" THEN concat(LEFT(phone,4),"-",substring(phone,4,4)) END END,"없음")) as phone, manager_name as managerName, manager_email as managerEmail, deposit_no as depositNo, deposit_host as depositHost, insert_userid as insertUserid, insert_day as insertDay, update_userid as updateUserid, update_day as updateDay from tb_customer where assets_flag = 'e' and name != '여비'
and delete_flag = #{vo.deleteFlag }
and insert_day >= #{vo.datepicker1 }
and insert_day <= #{vo.datepicker2 }
and no like '%${vo.no }%'
union select replace(left(no, 1), "S", "매출 거래처") as customerDiv, concat(substr(substr(no, 2), 1, 3), "-", substr(substr(no,2), 4, 2), "-" , substr(substr(no, 2), 6, 5)) as no, name as name, ceo as ceo, address as address, detail_address as detailAddress, conditions as conditions, item as item, corporation_no as corporationNo, open_date as openDate, jurisdiction_office as jurisdictionOffice, (IFNULL(CASE LEFT(phone,2) WHEN "02" THEN CASE CHAR_LENGTH(phone) WHEN "10" THEN concat("02","-",substring(phone,3,4),"-",RIGHT(phone,4)) WHEN "11" THEN concat("02","-",substring(phone,3,3),"-",RIGHT(phone,4)) END ELSE CASE CHAR_LENGTH(phone) WHEN "10" THEN concat(LEFT(phone,3),"-",substring(phone,4,3),"-",RIGHT(phone,4)) WHEN "11" THEN concat(LEFT(phone,3),"-",substring(phone,4,4),"-",RIGHT(phone,4)) WHEN "8" THEN concat(LEFT(phone,4),"-",substring(phone,4,4)) END END,"없음")) as phone, manager_name as managerName, manager_email as managerEmail, deposit_no as depositNo, deposit_host as depositHost, insert_userid as insertUserid, insert_day as insertDay, update_userid as updateUserid, update_day as updateDay from tb_salescustomer
where delete_flag = #{vo.deleteFlag }
and insert_day >= #{vo.datepicker1 }
and insert_day <= #{vo.datepicker2 }
and no like '%${vo.no }%'
order by corporationNo desc limit #{pagination.pageIndex }, #{pagination.listSize }

