select.if:
			
				select ((select count(*) as count
						   from tb_customer
			  			  where assets_flag!='e'
				

						    
					
						and delete_flag= #{deleteFlag }
					
				
				 		    
					
						and insert_day >= #{datepicker1 }
					
				
						    
					
						and insert_day <= #{datepicker2 }
					
				
				
					
						and no like '%${no }%'
						   
				
						  
			
						   ) +
					    (select count(*) as count
				    	   from tb_purchasecustomer
				

						    
					
						where delete_flag= #{deleteFlag }
					
				
				 		    
					
						and insert_day >= #{datepicker1 }
					
				
						    
					
						and insert_day <= #{datepicker2 }
					
				
				
					
						and no like '%${no }%'
						   
				
			
			
				    	   ) +
					    (select count(*) as count
				    	   from tb_salescustomer
				

						    
					
						where delete_flag= #{deleteFlag }
					
				
				 		    
					
						and insert_day >= #{datepicker1 }
					
				
						    
					
						and insert_day <= #{datepicker2 }
					
				
				
					
						and no like '%${no }%'
						   
				
			
			
				    	   
				    	   )) as count
			 	  from dual


	select count(*) as count
	  from tb_customer
	  where assets_flag != "e"


			    
		
			and delete_flag= #{deleteFlag }
		
	
			    
		
			and insert_day >= #{datepicker1 }
		
	
			    
		
			and insert_day <= #{datepicker2 }
		
	
	
		
			and no like '%${no }%'


	select count(*) as count
	  from tb_purchasecustomer


			    
		
			where delete_flag= #{deleteFlag }
		
	
			    
		
			and insert_day >= #{datepicker1 }
		
	
			    
		
			and insert_day <= #{datepicker2 }
		
	
	
		
			and no like '%${no }%'


	select count(*) as count
	  from tb_salescustomer


			    
		
			where delete_flag= #{deleteFlag }
		
	
			    
		
			and insert_day >= #{datepicker1 }
		
	
			    
		
			and insert_day <= #{datepicker2 }
		
	
	
		
			and no like '%${no }%'

select replace(left(c.no, 1), "A", "자산거래처") as customerDiv, (CASE CHAR_LENGTH(c.no) when "07" then c.no when "11" then concat(substr(substr(c.no, 2), 1, 3), "-", substr(substr(c.no,2), 4, 2), "-" , substr(substr(c.no, 2), 6, 5)) end) as no, c.name as name, c.ceo as ceo, c.address as address, c.detail_address as detailAddress, c.conditions as conditions, c.item as item, c.corporation_no as corporationNo, c.open_date as openDate, c.jurisdiction_office as jurisdictionOffice, (IFNULL(CASE LEFT(c.phone,2) WHEN "02" THEN CASE CHAR_LENGTH(c.phone) WHEN "10" THEN concat("02","-",substring(c.phone,3,4),"-",RIGHT(c.phone,4)) WHEN "11" THEN concat("02","-",substring(c.phone,3,3),"-",RIGHT(c.phone,4)) END ELSE CASE CHAR_LENGTH(c.phone) WHEN "10" THEN concat(LEFT(c.phone,3),"-",substring(c.phone,4,3),"-",RIGHT(c.phone,4)) WHEN "11" THEN concat(LEFT(c.phone,3),"-",substring(c.phone,4,4),"-",RIGHT(c.phone,4)) WHEN "8" THEN concat(LEFT(c.phone,4),"-",substring(c.phone,4,4)) END END,"없음")) as phone, c.manager_name as managerName, c.manager_email as managerEmail, b.deposit_no as depositNo, b.deposit_host as depositHost, c.insert_userid as insertUserid, date_format(c.insert_day, "%Y-%m-%d") as insertDay, c.update_userid as updateUserid, date_format(c.update_day, "%Y-%m-%d") as updateDay from tb_customer c left join tb_bank_account b on c.deposit_no = b.deposit_no where c.assets_flag != 'e'
and c.delete_flag = #{vo.deleteFlag }
and c.insert_day >= #{vo.datepicker1 }
and c.insert_day <= #{vo.datepicker2 }
and c.no like '%${vo.no }%'
union select replace(left(p.no, 1), "P", "매입 거래처") as customerDiv, concat(substr(substr(p.no, 2), 1, 3), "-", substr(substr(p.no,2), 4, 2), "-" , substr(substr(p.no, 2), 6, 5)) as no, p.name as name, p.ceo as ceo, p.address as address, p.detail_address as detailAddress, p.conditions as conditions, p.item as item, p.corporation_no as corporationNo, p.open_date as openDate, p.jurisdiction_office as jurisdictionOffice, (IFNULL(CASE LEFT(p.phone,2) WHEN "02" THEN CASE CHAR_LENGTH(p.phone) WHEN "10" THEN concat("02","-",substring(p.phone,3,4),"-",RIGHT(p.phone,4)) WHEN "11" THEN concat("02","-",substring(p.phone,3,3),"-",RIGHT(p.phone,4)) END ELSE CASE CHAR_LENGTH(p.phone) WHEN "10" THEN concat(LEFT(p.phone,3),"-",substring(p.phone,4,3),"-",RIGHT(p.phone,4)) WHEN "11" THEN concat(LEFT(p.phone,3),"-",substring(p.phone,4,4),"-",RIGHT(p.phone,4)) WHEN "8" THEN concat(LEFT(p.phone,4),"-",substring(p.phone,4,4)) END END,"없음")) as phone, p.manager_name as managerName, p.manager_email as managerEmail, b.deposit_no as depositNo, b.deposit_host as depositHost, p.insert_userid as insertUserid, date_format(p.insert_day, "%Y-%m-%d") as insertDay, p.update_userid as updateUserid, date_format(p.update_day, "%Y-%m-%d") as updateDay from tb_purchasecustomer p left join tb_bank_account b on p.deposit_no = b.deposit_no
where p.delete_flag = #{vo.deleteFlag }
and p.insert_day >= #{vo.datepicker1 }
and p.insert_day <= #{vo.datepicker2 }
and p.no like '%${vo.no }%'
union select replace(left(s.no, 1), "S", "매출 거래처") as customerDiv, concat(substr(substr(s.no, 2), 1, 3), "-", substr(substr(s.no,2), 4, 2), "-" , substr(substr(s.no, 2), 6, 5)) as no, s.name as name, s.ceo as ceo, s.address as address, s.detail_address as detailAddress, s.conditions as conditions, s.item as item, s.corporation_no as corporationNo, s.open_date as openDate, s.jurisdiction_office as jurisdictionOffice, (IFNULL(CASE LEFT(s.phone,2) WHEN "02" THEN CASE CHAR_LENGTH(s.phone) WHEN "10" THEN concat("02","-",substring(s.phone,3,4),"-",RIGHT(s.phone,4)) WHEN "11" THEN concat("02","-",substring(s.phone,3,3),"-",RIGHT(s.phone,4)) END ELSE CASE CHAR_LENGTH(s.phone) WHEN "10" THEN concat(LEFT(s.phone,3),"-",substring(s.phone,4,3),"-",RIGHT(s.phone,4)) WHEN "11" THEN concat(LEFT(s.phone,3),"-",substring(s.phone,4,4),"-",RIGHT(s.phone,4)) WHEN "8" THEN concat(LEFT(s.phone,4),"-",substring(s.phone,4,4)) END END,"없음")) as phone, s.manager_name as managerName, s.manager_email as managerEmail, b.deposit_no as depositNo, b.deposit_host as depositHost, s.insert_userid as insertUserid, date_format(s.insert_day, "%Y-%m-%d") as insertDay, s.update_userid as updateUserid, date_format(s.update_day, "%Y-%m-%d") as updateDay from tb_salescustomer s left join tb_bank_account b on s.deposit_no = b.deposit_no
where s.delete_flag = #{vo.deleteFlag }
and s.insert_day >= #{vo.datepicker1 }
and s.insert_day <= #{vo.datepicker2 }
and s.no like '%${vo.no }%'
order by customerDiv desc limit #{pagination.pageIndex }, #{pagination.listSize }

