mapper.select:
select count(*) from tb_voucher_manager as v join tb_voucher_item as i on v.no = i.voucher_no join tb_account as a on a.no = i.account_no join tb_voucher_system_mapping m on m.voucher_no = v.no left join (select no, name from tb_customer union select no, name from tb_salescustomer union select no, name from tb_purchasecustomer union select code as no, name from tb_bank ) as cu on cu.no = m.customer_no where v.use_yn = 1 and i.use_yn = 1 and m.use_yn = 1 and i.order_no = m.order_no and m.closing_yn = 'n'
and account_no = #{accountNo }
and (case char_length(cu.no) when"07" then (cu.no like '${customerNo }') when "11" then (cu.no like '%${customerNo }%') end)
and date_format(v.reg_date,'%Y-%m') = #{regDate }

select a.no as accountNo , a.name as accountName , v.reg_date as regDate , i.amount as amount , i.amount_flag as amountFlag , m.customer_no as customerNo , cu.name as customerName , m.voucher_use as voucherUse from tb_voucher_manager as v join tb_voucher_item as i on v.no = i.voucher_no join tb_account as a on a.no = i.account_no join tb_voucher_system_mapping m on m.voucher_no = v.no left join (select no, name from tb_customer union select no, name from tb_salescustomer union select no, name from tb_purchasecustomer union select code as no, name from tb_bank ) as cu on cu.no = m.customer_no where v.use_yn = 1 and i.use_yn = 1 and m.use_yn = 1 and i.order_no = m.order_no and m.closing_yn = 'n'
and account_no = #{vo.accountNo }
and (case char_length(cu.no) when"07" then (cu.no like '${vo.customerNo }') when "11" then (cu.no like '%${vo.customerNo }%') end)
and date_format(v.reg_date,'%Y-%m') = #{vo.regDate }
order by v.reg_date desc
limit #{pagination.pageIndex }, #{pagination.listSize }


select	v.no
  from tb_voucher_manager as v
  join tb_voucher_system_mapping m on v.no = m.voucher_no
 where v.reg_date >= #{End }
   and v.reg_date <= #{Start }
   and m.closing_yn ='y'
;

select sum(i.amount) as amount from tb_voucher_manager as v join tb_voucher_item as i on v.no = i.voucher_no join tb_account as a on a.no = i.account_no join tb_voucher_system_mapping m on m.voucher_no = v.no left join (select no, name from tb_customer union select no, name from tb_salescustomer union select no, name from tb_purchasecustomer union select code as no, name from tb_bank ) as cu on cu.no = m.customer_no where v.use_yn = 1 and i.use_yn = 1 and m.use_yn = 1 and i.order_no = m.order_no and m.closing_yn = 'n' and i.amount_flag = 'd'
and account_no = #{accountNo }
and (case char_length(cu.no) when"07" then (cu.no like '${customerNo }') when "11" then (cu.no like '%${customerNo }%') end)
and date_format(v.reg_date,'%Y-%m') = #{regDate }

