mapper.select:
select a.name as accountName, cu.name as customerName, v.reg_date as regDate, v.no as voucherNo, i.order_no as voucherOrderNo, m.insert_team as insertTeam, m.insert_userid as insertUserid, m.voucher_use as voucherUse, c.card_no as cardNo, c.user as cardUser, ifnull(ba.bank_name, c.bank_name) as bankName, ifnull(ba.bank_code, c.bank_code) as bankCode, ba.deposit_no as depositNo, ba.deposit_host as depositHost, case when amount_flag="c" then "차변" when amount_flag="d" then "대변" end as amountFlag, i.amount as amount from tb_voucher_manager v join tb_voucher_item i on v.no = i.voucher_no join tb_voucher_system_mapping m on i.voucher_no = m.voucher_no join tb_account a on a.no = i.account_no left join (select no, name from tb_customer union select no, name from tb_salescustomer union select no, name from tb_purchasecustomer union select code as no, name from tb_bank) as cu on cu.no = m.customer_no left join tb_cardmanager c on c.card_no = m.card_no left join tb_bank_account ba on ba.deposit_no = m.deposit_no where v.use_yn = 1 and i.use_yn = 1 and m.use_yn = 1
and m.insert_day >= #{datepicker1 }
and m.insert_day <= #{datepicker2 }
and a.code = #{accountCode }
and cu.no = #{customerNo }
order by v.no desc, i.order_no ;
