mapper.select:

select purchase_no as no, number, date_format(purchase_date, '%Y-%m-%d') as purchaseDate, purchase_manager as purchaseManager, customer_code as customerCode, customer_name as customerName
	  , item_code as itemCode, item_name as itemName, quantity, supply_value as supplyValue, tax_value as taxValue, taxbill_no as taxbillNo,
	  date_format(receipt_date, '%Y-%m-%d') as receiptDate, date_format(release_date, '%Y-%m-%d') as releaseDate 
from tb_purchasemanagement
where delete_flag = #{deleteFlag }

	
	
	
    	and purchase_date = #{purchaseDate }
    
    
  	
  	
	
    	and purchase_no = #{no }
    
  	
  	
	
    	and customer_code = #{customerCode }
    
  	
  	
	
    	and customer_name = #{customerName }
    
  	
  	
	
    	and item_code = #{itemCode }
    
  	
  	
	
    	and item_name = #{itemName }
    
  	
  	
  		order by #{orderStd }
  	
  		limit #{page},11
