mapper.select:

	select count(*) 
	from tb_land, tb_section, tb_customer  
	where tb_land.section_no = tb_section.code
	
		
		
		 and tb_land.id like concat('%', #{id },'%')
		
		
		
		
		 and tb_section.classification = #{sectionName }
		
			
		
		
		 and tb_land.pay_date >= '${startDate } 00:00:00' and tb_land.pay_date <= '${endDate } 23:59:59'
		
		
		
		
		 and tb_customer.name like concat('%',#{customerName },'%')
		
		
		
		
		 and tb_customer.manager_name like concat('%',#{managerName },'%')
		
		
		
		
		 and tb_land.land_area<=#{landArea }
		
		
		
		
		 and tb_land.wide_address=#{wideAddress }
		
		
		
		
		 and tb_land.city_address=#{cityAddress }
		
		
		
		
		 and tb_land.acq_price <= #{acqPrice }
		
		
		
		
		 and tb_land.flag in("s", "o") 
		
		
		
		 and tb_section.parent_no=2 and tb_land.customer_no = tb_customer.no and tb_customer.assets_flag="a"

select tb_land.id, tb_land.customer_no as customerNo, tb_customer.name as customerName, tb_customer.manager_name as managerName, tb_land.taxbill_no as taxbillNo, tb_land.voucher_no as voucherNo, tb_land.section_no as sectionNo, tb_section.classification as sectionName, tb_land.land_area as landArea, tb_land.combine_no as combineNo, tb_land.owner_name as ownerName, tb_land.wide_address as wideAddress, tb_land.city_address as cityAddress, tb_land.detail_address as detailAddress, tb_land.public_value as publicValue, tb_land.acq_price as acqPrice, tb_land.etc_cost as etcCost, tb_land.reg_tax as regTax, tb_land.acq_tax as acqTax, tb_land.pay_date as payDate, tb_land.tax_kind as taxKind, tb_land.flag as flag from tb_land, tb_section, tb_customer where tb_land.section_no = tb_section.code
and tb_land.id like concat('%', #{id },'%')
and tb_section.classification = #{sectionName }
and tb_land.pay_date >= '${startDate } 00:00:00' and tb_land.pay_date <= '${endDate } 23:59:59'
and tb_customer.name like concat('%',#{customerName },'%')
and tb_customer.manager_name like concat('%',#{managerName },'%')
and tb_land.land_area<=#{landArea }
and tb_land.wide_address=#{wideAddress }
and tb_land.city_address=#{cityAddress }
and tb_land.acq_price <= #{acqPrice }
and tb_land.flag IN("s", "o")
and tb_section.parent_no=2 and tb_land.customer_no = tb_customer.no and tb_customer.assets_flag="a" group by tb_land.id order by tb_land.update_day desc, tb_land.insert_day desc limit #{pagination.pageIndex }, #{pagination.listSize }
