mapper.select:
select id, customer_no as customerNo, name as customerName, manager_name as managerName, taxbill_no as taxbillNo, voucher_no as voucherNo, section_no as sectionNo, classification as sectionName, area, combine_no as combineNo, owner_name as ownerName, wide_address as wideAddress, city_address as cityAddress, b.detail_address as detailAddress, floor, basement, purpose, material, public_value as publicValue, acq_price as acqPrice, etc_cost as etcCost, acq_tax as acqTax, pay_date as payDate, tax_kind as taxKind, b.flag
from tb_building as b left outer join tb_section on b.section_no = tb_section.code left outer join tb_customer on b.customer_no = tb_customer.no
where tb_section.parent_no = 3 and b.id like concat('%',#{vo.id },'%') and b.customer_no like concat('%',#{vo.customerNo },'%') and b.section_no like concat('%',#{vo.sectionNo },'%') and b.wide_address like concat('%',#{vo.wideAddress },'%') and b.city_address like concat('%',#{vo.cityAddress },'%') and b.detail_address like concat('%',#{vo.detailAddress },'%') and b.flag like concat('%',#{vo.flag },'%')
and b.area <= #{vo.area }
and b.acq_price <= #{vo.acqPrice }
and b.pay_date >= '${startDate } 00:00:00' and b.pay_date <= '${endDate } 23:59:59'
order by b.insert_day desc
limit #{pagination.pageIndex }, #{pagination.listSize }

select count(*)
from tb_building as b left outer join tb_section on b.section_no = tb_section.code left outer join tb_customer on b.customer_no = tb_customer.no
where tb_section.parent_no = 3 and b.id like concat('%',#{vo.id },'%') and b.customer_no like concat('%',#{vo.customerNo },'%') and b.section_no like concat('%',#{vo.sectionNo },'%') and b.wide_address like concat('%',#{vo.wideAddress },'%') and b.city_address like concat('%',#{vo.cityAddress },'%') and b.detail_address like concat('%',#{vo.detailAddress },'%') and b.flag like concat('%',#{vo.flag },'%')
and b.area <= #{vo.area }
and b.acq_price <= #{vo.acqPrice }
and b.pay_date >= '${startDate } 00:00:00' and b.pay_date <= '${endDate } 23:59:59'
