mapper.select:

select id
  from tb_intangible_assets 
 where id = #{id }

select tb.id, tb.customer_no as customerNo, tb.taxbill_no as taxbillNo, tb.voucher_no as voucherNo, tb.taxbill_no as taxbillNo, tb.name, tb.section_no as sectionNo, tb.code as code, s.classification as classification, tb.copy_count as copyCount, tb.purpose, tb.address, tb.user, tb.acq_price as acqPrice, tb.addi_fee as addiFee, tb.pay_date as payDate, tb.tax_kind as taxKind, c.name as customerName, c.manager_name as customerManager, tb.flag, tb.insert_userid as insertUserId, date_format(tb.insert_day, '%Y-%m-%d %h:%i:%s') as insertDay, tb.update_userid as updateUserId, tb.update_day as updateDay from tb_intangible_assets tb left outer join tb_section s on tb.code = s.code left outer join tb_customer c on tb.customer_no = c.no where s.parent_no = 5 and tb.flag in ('s', 'o')
and id like CONCAT('%',#{id },'%')
order by tb.insert_day desc limit #{pagination.pageIndex }, #{pagination.listSize }


select deposit_no as depositNo, bank_code as bankCode, bank_name as bankName
  from tb_customer
 where no = #{customerNo }


select classification, account_no as accountNo
  from tb_purpose
 where classification = #{purpose }


select voucher_no as voucherNo
  from tb_intangible_assets
 where id = #{id }

mapper.delete:

update tb_intangible_assets 
	  set flag = 'd'
	where id = #{id }
