mapper.select:
select tb.id, tb.customer_no as customerNo, tb.taxbill_no as taxbillNo, tb.voucher_no as voucherNo, tb.taxbill_no as taxbillNo, tb.name, tb.section_no as sectionNo, tb.code as code, s.classification as classification, tb.copy_count as copyCount, tb.purpose, tb.address, tb.user, tb.acq_price as acqPrice, tb.addi_fee as addiFee, tb.pay_date as payDate, tb.tax_kind as taxKind, c.name as customerName, c.manager_name as customerManager, tb.flag, tb.insert_userid as insertUserId, date_format(tb.insert_day, '%Y-%m-%d %h:%i:%s') as insertDay, tb.update_userid as updateUserId, tb.update_day as updateDay from tb_intangible_assets tb left outer join tb_section s on tb.code = s.code left outer join tb_customer c on tb.customer_no = c.no where s.parent_no = 5 and flag ='s'
and id like CONCAT('%',#{id },'%')
and c.name like CONCAT('%',#{customerName },'%')
and tb.address like CONCAT('%',#{address },'%')
and s.classification like CONCAT('%',#{classification },'%')
and c.manager_name like CONCAT('%',#{customerManager },'%')
and date_format(tb.pay_date, '%Y-%m-%d') = #{payDate }
and tb.acq_price <= ${acqPrice }
limit #{pagination.pageIndex }, #{pagination.listSize }

select tb.id, tb.customer_no as customerNo, tb.taxbill_no as taxbillNo, tb.voucher_no as voucherNo, tb.taxbill_no as taxbillNo, tb.name, tb.section_no as sectionNo, tb.code as code, s.classification as classification, tb.copy_count as copyCount, tb.purpose, tb.address, tb.user, tb.acq_price as acqPrice, tb.addi_fee as addiFee, tb.pay_date as payDate, tb.tax_kind as taxKind, c.name as customerName, c.manager_name as customerManager, tb.flag, tb.insert_userid as insertUserId, date_format(tb.insert_day, '%Y-%m-%d %h:%i:%s') as insertDay, tb.update_userid as updateUserId, tb.update_day as updateDay from tb_intangible_assets tb left outer join tb_section s on tb.code = s.code left outer join tb_customer c on tb.customer_no = c.no where s.parent_no = 5
and id like CONCAT('%',#{id },'%')
and c.name like CONCAT('%',#{customerName },'%')
and tb.address like CONCAT('%',#{address },'%')
and s.classification like CONCAT('%',#{classification },'%')
and c.manager_name like CONCAT('%',#{customerManager },'%')
and date_format(tb.pay_date, '%Y-%m-%d') = #{payDate }
and tb.acq_price lte #{acqPrice }
limit #{pagination.pageIndex }, #{pagination.listSize }
