mapper.select:
select s.*, bc.deposit_host as depositHost, b.name as bankName from tb_s_term_debt s, tb_bank_account bc, tb_bank b where s.deposit_no = bc.deposit_no and s.bank_code = b.code and s.delete_flag="N" and s.repay_comple_flag="N"
and s.code LIKE CONCAT(#{code}, "%")
and year(s.debt_date) = #{financialYear}
order by insert_date desc limit ${startRow}, ${pageSize}

select count(*) from tb_s_term_debt where delete_flag="N" and repay_comple_flag="N"
and code LIKE CONCAT(#{code}, '%')
and year(debt_date) = #{financialYear}


	select count(*)
	from tb_repay
	where code=#{_parameter}

mapper.update:

	update tb_s_term_debt 
	set repay_bal = ${repayBal}, repay_comple_flag= #{repayCompleFlag}
	where no = ${no}
