mapper.select:
select a.sales_no as salesNo, date_format(a.sales_date, '%Y-%m-%d') as salesDate, date_format(a.release_date, '%Y-%m-%d') as releaseDate, a.emp_manager as empManager, a.customer_code as customerCode, a.customer_name as customerName, b.phone as customerPhone, a.taxbill_no as taxbillNo, a.number as number, a.item_code as itemCode, a.item_name as itemName, a.quantity as quantity, a.supply_value as supplyValue, a.tax_value as taxValue from tb_sales a, tb_salescustomer b where a.customer_code = b.no
and a.sales_date >= '${startDate } 00:00:00' and a.sales_date <= '${endDate } 23:59:59'
and a.sales_no = #{salesNo }
and a.customer_code = #{customerCode }
and a.insert_userid = #{insertUserid }
and a.item_code = #{itemCode }
and a.delete_flag = #{deleteFlag }
order by ${orderData} asc;

select	count(*) as cnt
  from	tb_sales
;
