mapper.select:

select count(*)
from tb_salescustomer
where 1=1


	
	and delete_flag = #{deleteFlag }
		


	
	and item like concat('%', #{item }, '%')
		


	
	and insert_day = #{insertDay }
		


	
	and manager_name = #{managerName }
		


	
	and substring(no, 2) >= #{no }
		


	
	and substring(no, 2) <= #{preNo }

select substring(s.no, 2) as no, s.name as name, s.ceo as ceo, s.corporation_no as corporationNo, s.zip_code as zipCode, s.address as address, s.detail_address as detailAddress, ifnull(s.zip_code, concat( "(", s.zip_code, ") ",s.address, " ", s.detail_address)) as addressAll, s.phone as phone, s.conditions as conditions, s.item as item, date_format(s.open_date, "%Y-%m-%d") as openDate, s.jurisdiction_office as jurisdictionOffice, s.manager_name as managerName, s.manager_email as managerEmail, b.deposit_no as depositNo, b.deposit_host as depositHost, b.bank_code as bankCode, b.bank_name as bankName, case when s.delete_flag = 'Y' then '삭제' when s.delete_flag = 'N' then '' end as deleteFlag, s.insert_userid as insertUserid, date_format(s.insert_day, "%Y-%m-%d %T") as insertDay, s.update_userid as updateUserid, date_format(s.update_day, "%Y-%m-%d %T") as updateDay from tb_salescustomer s left join tb_bank_account b on s.deposit_no = b.deposit_no where 1=1
and s.delete_flag = #{customer.deleteFlag }
and s.item like concat('%', #{customer.item }, '%')
and s.insert_day = #{customer.insertDay }
and s.manager_name = #{customer.managerName }
and substring(s.no, 2) >= #{customer.no }
and substring(s.no, 2) <= #{customer.preNo }
order by s.no limit #{pagination.pageIndex }, #{pagination.listSize }
