mapper.select:

select count(*)
from tb_sales
where 1=1


	
	and date_format(sales_date, '%Y-%m') = #{salesDate }
		


	
	and substring(customer_code, 2) = #{customerCode }

select sales_no as salesNo , number , date_format(sales_date, '%Y-%m-%d') as salesDate , emp_manager as empManager , substring(customer_code, 2) as customerCode , customer_name as customerName , item_code as itemCode , item_name as itemName , quantity , supply_value as supplyValue , tax_value as taxValue , tax_type as taxType , taxbill_no as taxbillNo , date_format(release_date, '%Y-%m-%d') as releaseDate from tb_sales where 1=1
and date_format(sales_date, '%Y-%m') = #{sales.salesDate }
and substring(customer_code, 2) = #{sales.customerCode }
order by salesDate limit #{pagination.pageIndex }, #{pagination.listSize }
