mapper.insert:
insert	into tb_closing_date (
	closing_year_month
    , closing_date
    , closing_statement_date
    , closing_purchase_date
    , closing_sales_date
    , closing_assets_date
    , closing_debt_date
    , closing_settlement_date
    , insert_userid
) values (
	#{closingYearMonth }
	, #{closingDate }
    , #{closingStatementDate }
    , #{closingPurchaseDate }
    , #{closingSalesDate }
    , #{closingAssetsDate }
    , #{closingDebtDate }
    , #{closingSettlementDate }
    , #{insertUserid }
);

	select last_insert_id()
