mapper.select:
select m.no as no , m.account_order as accountOrder , m.account_no as accountNo , a.balance_type as balanceType , a.name as accountName , m.account_usedyear as accountUsedyear , m.account_statement_type as accountStatementType , m.use_yn as useYn , m.insert_userid as insertUserid , date_format(m.insert_day,'%Y-%m-%d %h:%i:%s') as insertDay , m.update_userid as updateUserid , date_format(m.update_day,'%Y-%m-%d %h:%i:%s') as updateDay from tb_account_management m, tb_account a where a.no = m.account_no and m.use_yn = 1
and m.account_order = #{accountOrder }
and m.account_no = #{accountNo }
and m.account_statement_type = #{accountStatementType }
and m.account_usedyear = #{year }
order by m.account_order limit #{pagination.pageIndex }, #{pagination.listSize }

	
	  select count(m.no) as cnt
	    from tb_account_management m, tb_account a
	   where a.no = m.account_no
	     and m.use_yn = 1
	 
	 
         and m.account_order = #{accountOrder }
     
	 
         and m.account_no = #{accountNo }
     
	 
         and m.account_statement_type = #{accountStatementType }
     
	 
         and m.account_usedyear = #{accountUsedyear }
     
	   order by account_order

mapper.update:

update tb_account_management 
   set account_order    = #{accountOrder}
     , account_no       = #{accountNo}
	 , account_usedyear = #{accountUsedyear}  
	 , update_userid    = #{updateUserid} 
	 , update_day       = now()
 where no= #{no}


update tb_account_management 
   set use_yn = 0 
 where no= #{no}
