mapper.update:
update	tb_closing_date
   set	update_userid = #{updateUserid }
		, update_day = now()
        , closing_yn = true
 where	no = #{closingDateNo }
;

mapper.insert:
insert	into tb_trial_balance(
    closing_date_no
    , account_no
    , debtor_total
    , credit_total
    , debtor_spot_month
    , credit_spot_month
    , insert_userid
) values (
	#{closingDateNo }
    , #{accountNo }
    , #{debtorTotal }
    , #{creditTotal }
    , #{debtorSpotMonth }
    , #{creditSpotMonth }
    , #{insertUserid }
);

	select last_insert_id()
